import { Component } from '@angular/core';

interface Feature {
  title: string;
  icon: string;
  bgColor: string;
}

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {
  activeLink: string = 'home';

  setActiveLink(link: string) {
    this.activeLink = link;
  }
  menuOpen = false;

  toggleMenu() {
    this.menuOpen = !this.menuOpen;
  }


  features: Feature[] = [
    { title: 'Research', icon: 'assets/Icons/research.png', bgColor: '#FFE4E1' },
    { title: 'Design', icon: 'assets/Icons/design.png', bgColor: '#FFEFDB' },
    { title: 'Develop', icon: 'assets/Icons/develop.png', bgColor: '#E0F7FA' },
    { title: 'Test', icon: 'assets/Icons/test.png', bgColor: '#E3F2FD' },
    { title: 'Elevate', icon: 'assets/Icons/elevate.png', bgColor: '#FFF3E0' }
  ];


}
